import './App.css';
import React from 'react'
import { useState} from 'react';

function App() {
  const [update, setUpdate] = useState(0);
  // console.log(update)

  let prevUpdate=()=>{
    setUpdate(update-1)
    if(update.length<=0){
      update.style.color='red'
    }
  }

  let nextUpdate=()=>{
    setUpdate(update+1)
  }

  return (
    <div className="App">
      <button onClick={prevUpdate}>-</button>
      <p>{update}</p>
      <button onClick={nextUpdate}>+</button>
    </div>
  );
}

export default App;
