import './App.css';
import React from 'react'
import { useState} from 'react';

function App() {
  const [update, setUpdate] = useState(0);
  const[color,setColor]=useState('white');
  // console.log(update)

  let prevUpdate=()=>{
    if(update===0){
      setUpdate(update-1)
      // document.getElementById('counting').style.color="red";
      setColor(document.getElementById('counting').style.color='red')
    }
  }

  let nextUpdate=()=>{
    setUpdate(update+1)
  }

  return (
    <div className="App">
      <button onClick={prevUpdate}>-</button>
      <p id='counting'>{update}</p>
      <button onClick={nextUpdate}>+</button>
    </div>
  );
}

export default App;
