import React from "react";

export default function MovieCards(props) {
    return (
        <>
            <div className="card" style={{border:'2px solid red',width: "350px",height:"100%"}}>
                <img style={{width:"350px"}} src={`https://image.tmdb.org/t/p/original${props.image}`} className="card-img-top" alt={props.title}/>
                    <div className="card-body" style={{textAlign:'center'}}>
                        <p className="card-text">{props.text}</p>
                    </div>
            </div>
        </>
    )
}
