import './App.css';
import React, { useState, useEffect } from 'react'
import MovieCard from './movieCards';
// import Navbar from './navbar';
// import App from './App';


// https://movie-task.vercel.app/api/popular?page=1
//const api_url = "https://movie-task.vercel.app/api/popular?page=1";
// const api_url = "https://movie-task.vercel.app/api/movie?movieId=634649"
const api_url="https://movie-task.vercel.app/api/search?page=1&query="



function App() {
  const [movies, setMovies] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");


  const searchMovies = async (title) => {
    // const response = await fetch(`${api_url}&s=${title}`);
    const response = await fetch(`${api_url}${title}`);

    const data = await response.json();
    setMovies(data.data.results);
    // console.log(data.data.results);

  }
  useEffect(() => {
    searchMovies(handleSearch);
    // console.log(searchMovies)
  }, []);

  let handleSearch=(event)=>{
    let uservalue=event.target.value;
    setSearchTerm(uservalue)
    console.log(uservalue)
    // setSearchTerm(event.target.value);
    // searchTerm(event.target.value);
    // console.log(event.target.value)
  }


  return (
    <>

      {/* <Navbar searchBar={searchBar} searchTerm={searchTerm}></Navbar> */}


      <nav className="navbar bg-light">
        <div className="container-fluid">
          <a className="navbar-brand" href='/#'>Movies</a>
          <form className="d-flex" role="search">

            <input className="form-control me-2" type="search" value={searchTerm}
              onChange={handleSearch}
              placeholder="Search for movies" aria-label="Search" />

            <button className="btn btn-outline-success" type="submit">Search</button>
          </form>
        </div>
      </nav>

      {movies.length > 0 ? (
        <div className="container">
          {movies.map((movie) => (
            <MovieCard movie={movie} />
          ))}
        </div>
      ) : (
        <div className="empty">
          <h2>No movies found</h2>
        </div>
      )}
    </>
  );
}

export default App;
