import React from 'react';

const MovieCard = ({ movie: { id, year, poster_path, overview, title } }) => {
    return (
        <div className="movie" key={id}>
            <div>
                <p>{year}</p>
            </div>

            <div>
                <img src={poster_path !== "N/A" ? poster_path : "https://via.placeholder.com/400"} alt={overview} />
            </div>

            <div>
                <span>{title}</span>
                <h3>{overview}</h3>
            </div>
        </div>
    );
}

export default MovieCard;
