import './App.css';
import React from 'react'
import { useEffect } from 'react';
import MovieCards from './movieCards';
import Navbar from './navbar';

// https://movie-task.vercel.app/api/popular?page=1
const api_url = "https://movie-task.vercel.app/api/popular?page=1";


function App() {

  const movie1 = {

    "adult": false,
    "backdrop_path": "/jsoz1HlxczSuTx0mDl2h0lxy36l.jpg",
    "genre_ids": [
      14,
      28,
      35
    ],
    "id": 616037,
    "original_language": "en",
    "original_title": "Thor: Love and Thunder",
    "overview": "After his retirement is interrupted by Gorr the God Butcher, a galactic killer who seeks the   extinction of the gods, Thor Odinson enlists the help of King Valkyrie, Korg, and ex-girlfriend Jane Foster, who now wields Mjolnir as the Mighty Thor. Together they embark upon a harrowing cosmic adventure to uncover the mystery of the God Butcher’s vengeance and stop him before it’s too late.",
    "popularity": 1920.283,
    "poster_path": "/pIkRyD18kl4FhoCNQuWxWu5cBLM.jpg",
    "release_date": "2022-07-06",
    "title": "Thor: Love and Thunder",
    "video": false,
    "vote_average": 6.8,
    "vote_count": 4085

  }

  const seachMovies = async (title) => {
    const response = await fetch(`${api_url}&s=${title}`);
    const data = await response.json();
    console.log(data.data.results);
  }



  useEffect(() => {
    seachMovies()
  }, [])

  return (
    <>
      <Navbar></Navbar>
      <MovieCards title={movie1.title} text={movie1.overview} image={movie1.poster_path}></MovieCards>
    </>
  );
}

export default App;
